    # Application event handlers
    if ($sync.WPFInstallSelectedApps) {
        $sync.WPFInstallSelectedApps.Add_Click({
                try {
                    Invoke-WPFApplicationsAsync
                }
                catch {
                    Write-Logger "Error in Install Selected Apps: $($_.Exception.Message)" "ERROR"
                }
            })
